SELECT DISTINCT 
(SUBSTR(STR(?GdprControl), STRLEN(STR(iot-reg:)) + 1) AS ?GdprControlValue) 
?obligationLabel 
?permissionLabel 
?prohibitionLabel 
?dispensationLabel
WHERE {
    ?deletion rdf:type iot-reg:DataDeletion ;
